  'driver_Extra_Km_Charge':driver_Extra_Km_Charge,            'driver_Nic':driver_Nic,            'driver_Licence':driver_Licence,            'driver_Other_Charge':driver_Other_Charge,            'driver_Blood_Group':driver_Blood_Group,            'driver_Telephone':driver_Telephone        };        var postList={            'post':post,            'ctrList':ctrlist        };        return postList;    }    function getPostDataSearch(ctrlist){        var driverName=$('#'+ctrlist[0]).val();        var post={            'driver_Name':driverName        };        var postList={            'post':post,            'ctrList':ctrlist        };        return postList;    }</script><?php $this->load->view('master/footer') ?><!-- Jquery Datatable scripts--><script src="<?php echo base_url();?>assets/libs/DataTables-1.10.9/js/jquery.dataTables.min.js"></script><script src="<?php echo base_url();?>assets/libs/DataTables-1.10.9/js/dataTables.bootstrap.min.js"></script><!-- page scripts --><script src="<?php echo base_url();?>assets/scripts/master/integrate.js"></script><script>$(function () {$('[data-toggle="tooltip"]').tooltip()})</script><!--stat loader --><?php$this->load->view('tools/loader',$ctrlData);?></body></html>